function App() {
	const [note, setNote] = useState('')
	const [notes, setNotes] = useState([
		{
			_id: '1',
			content: 'asdasdgjhasgd',
			createdAt: 'djagsdahdj',
		},
		{ _id: '2', content: 'asdasdgjhasgd', createdAt: 'djagsassddahdj' },
		{
			_id: '3',
			content: 'asdasdgjhasgd',
			createdAt: 'djagsdahdj',
		},
	])

	const handleMoreNote = () => {
		console.log(note)
	}

	return (
		<div>
			<h1 className='title'>Notes Management</h1>
			<div className='more-container'>
				<input
					type='search'
					className='more'
					placeholder='More'
					value={note}
					onChange={(e) => setNote(e.target.value)}
				/>
				<button className='more-btn' onClick={() => handleMoreNote()}>
					More
				</button>
			</div>
			<div className='notes-container'>
				{notes &&
					notes.map((note) => (
						<div className='note' key={note._id}>
							<div className='w-full'>
								<p className='content'>{note.content}</p>
								<p className='createdAt'>{note.createdAt}</p>
							</div>
							<button
								className='delete-btn'
								onClick={() => {
									console.log(note._id)
								}}>
								X
							</button>
						</div>
					))}
			</div>
		</div>
	)
}

export default App
